package conditional;

public class SwitchDemo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str="Happy";
		
		switch(str)
		{
		case "new":
			System.out.println("New");
			break;
		case "year":
			System.out.println("Year");
			break;
		case"happy":
			System.out.println("Happy");
			break;
		default:System.out.println("Welcome");
		}
	}

}
